function css_frame() {
    return {
        height: '100%',
        width:  '100%',
        padding:'0',
        position: 'fixed',
        top:    '0',
        left:   '0',
        zIndex: '999',
        border: '0'
    }
}

function css_div_default_height() {
    return {
        height: '50px'
    }
}

function css_div() {
    return {
        height: '50px',
        width:  '680px',
        position: 'fixed',
        top: '20%',
        margin: '0',
        padding: '0',
        left: '50%',
        MozTransform: 'translateX(-50%)',
        webkitTransform: 'translateX(-50%)',
        zIndex: '1000',
        border: '0px',
        borderRadius: '10px',
        boxShadow: '0 0 0 1px rgba(20, 20, 20, .5)',
        outline: '0',
        background: 'white'
    }
}

function css_input() {
    return {
        height: '50px',
        width:  '680px',
        margin: '0',
        padding: '0',
        paddingLeft: '20px',
        top: '0%',
        left: '0%',
        zIndex: '1001',
        border: '0px',
        borderRadius: '10px',
        outline: '0',
        fontFamily: '"HelveticaNeue-Light", "Helvetica Neue Light", "Helvetica Neue", Helvetica, Arial, "Lucida Grande", sans-serif',
        fontSize:   '20px',
        color: '#111',
    }
}

function css_ul() {
    return {
        listStyleType: 'none',
        fontFamily: '"HelveticaNeue-Light", "Helvetica Neue Light", "Helvetica Neue", Helvetica, Arial, "Lucida Grande", sans-serif',
        padding: '0',
        margin: '0',
    }
}

function css_tab_div() {
    return {
        height: '50px',
        width:  '660px',
        margin: '0px',
        padding: '0',
        boxShadow: '0 -0.2px 0 #000',
        position: 'relative',
        left: '50%',
        MozTransform: 'translateX(-50%)',
        webkitTransform: 'translateX(-50%)',
        outline: '0',
        border: '0',
    }
}

function css_tab_table() {
    return {
        cellpadding: '0',
        cellspacing: '0',
        height:'100%',
        width:'100%'
    }
}

function css_favicon() {
    return {
        height: '20px',
        width: '20px'
    }
}

function css_tab_info_table() {
    return {
        width:'100%',
        height: '100%'
    }
}

function css_tab_title_td() {
    return {
        height : '25px',
        verticalAlign: 'middle'
    }
}

function css_tab_url_td() {
    return {
        height : '5px',
        verticalAlign: 'middle',
        color: '#777',
        fontSize: '7pt'
    }
}